item.description:
WHERE LastStatus LIKE &#039;%The subscription contains parameter values that are not valid.%&#039;
	OR LastStatus LIKE &#039;%Error:%&#039;
	OR LastStatus LIKE &#039;%Failure%&#039;
	OR LastStatus LIKE &#039;%The e-mail address of one or more recipients is not valid.%&#039;
	OR (LastStatus like &#039;%; % errors.&#039; and LastStatus not like &#039;%; 0 errors.&#039;)
