if(!empty($_GET[&#039;page&#039;])) {
    $page = filter_input(INPUT_GET, &#039;page&#039;, FILTER_VALIDATE_INT);
    if(false === $page) {
        $page = 1;
    }
}

// set the number of items to display per page
$items_per_page = 4;

// build query
$offset = ($page - 1) * $items_per_page;
$sql = &quot;SELECT * FROM menuitem LIMIT &quot; . $offset . &quot;,&quot; . $items_per_page;

