         THEN (LEN(CONVERT(NVARCHAR(MAX),s2.TEXT)) * 2)&lt;br /&gt;
         ELSE statement_end_offset END)  - statement_start_offset) / 2+1))  AS sql_statement,&lt;br /&gt;
       last_execution_time&lt;br /&gt;
